mapper.insert:

/*	qnaWrite	*/
	INSERT INTO TB_QNA
		(QNA_NO
		, QNA_TITLE
		, QNA_CONTENT
		, QNA_REG_ID
		, QNA_REG_DATE				
		, QNA_MOD_DATE				
	) VALUES 
		('A' || LPAD(QNA_SEQ.NEXTVAL, 5, 0) /* N*/
		, #{qaTitle}
		, #{qaContent}
		, #{qaRegId}
		, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
		, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
	)

mapper.update:

/*	qnaModify */

	UPDATE TB_QNA
	SET QNA_TITLE         =  #{qaTitle}
	  , QNA_CONTENT       =  #{qaContent}
	  , QNA_MOD_DATE	  =  TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')  
	WHERE QNA_NO = #{qaNo}

mapper.delete:

/*	deleteQnA	*/
	DELETE
	FROM  TB_QNA
	WHERE 1=1
	AND QNA_NO = #{qaNo}


/*	deleteqna2	*/
	DELETE
	FROM  TB_QNA
	WHERE 1=1
	AND QNA_REG_ID = #{qaRegId}
